import './styles.css';

import ResultCard from 'components/ResultCard';
import { useState } from 'react';
import { type } from 'os';
import axios from 'axios';

type FormData = {
  user: string;
};

type Github = {
  url: string;
  followers: string;
  location: string;
  name: string;
  avatar_url: 'https://avatars.githubusercontent.com/u/13897257?v=4';
};

const Search = () => {
  const [github, setGithub] = useState<Github>();
  const [formData, setFormData] = useState<FormData>({ user: '' });
  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    axios
      .get(`https://api.github.com/${formData.user}/acenelio`)
      .then((response) => {
        setGithub(response.data);
      })
      .catch((error) => {
        setGithub(undefined);
      });
  };
  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const name = event.target.name;
    const value = event.target.value;
    setFormData({ ...formData, [name]: value });
  };

  return (
    <div className="search-container">
      <div className="container search-card">
        <h1>Encontre um perfil Github</h1>
        <form onSubmit={handleSubmit}>
          <div className="form-container">
            <input
              type="text"
              name="user"
              value={formData.user}
              className="search-input"
              placeholder="UsuÃ¡rio Github"
              onChange={handleChange}
            />
            <button type="submit" className="btn search-button">
              Encontrar
            </button>
          </div>
        </form>
      </div>
      {github && (
        <>
          <ResultCard title="Perfil" description={github.url} />
          <ResultCard title="Seguidores" description={github.followers} />
          <ResultCard title="Localidade" description={github.location} />
          <ResultCard title="Nome" description={github.name} />
        </>
      )}
    </div>
  );
};

export default Search;
