import './styles.css';

import ResultCard from 'components/ResultCard';
import { useState } from 'react';
import { type } from 'os';

type FormData = {
  user: string;
};

type Github = {
  url: 'https://api.github.com/users/acenelio';
  followers: 7318;
  location: 'Brazil';
  name: 'Nelio Alves';
  avatar_url: 'https://avatars.githubusercontent.com/u/13897257?v=4';
};

const Search = () => {
  const [formData, setFormData] = useState<FormData>({ user: '' });
  const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {};
  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const name = event.target.name;
    const value = event.target.value;
    setFormData({ ...formData, [name]: value });
  };

  return (
    <div className="search-container">
      <div className="container search-card">
        <h1>Encontre um perfil Github</h1>
        <form onSubmit={handleSubmit}>
          <div className="form-container">
            <input
              type="text"
              name="user"
              value={formData.user}
              className="search-input"
              placeholder="UsuÃ¡rio Github"
              onChange={handleChange}
            />
            <button type="submit" className="btn search-button">
              Encontrar
            </button>
          </div>
        </form>
      </div>
      <div className="result-card">
        <ResultCard title="Perfil" description="tal" />
        <ResultCard title="Seguidores" description="234" />
        <ResultCard title="Localidade" description="maceio" />
        <ResultCard title="Nome" description="fabio" />
      </div>
    </div>
  );
};

export default Search;
